import { useState } from "react";
import "./ExpenseForm.css";
import { initialObject } from "./Object";

export default function ExpenseForm() {
  // const [title, setTitle] = useState("");
  // const [amount, setAmount] = useState("");
  // const [date, setDate] = useState("");

  const [userImput, setUserImput] = useState(initialObject);

  const imputChangeHandler = (event) => {
    const { name, value } = event.target;

    // setUserImput(() => ({
    //   ...userImput,
    //   [name]: value,
    // }));
    setUserImput((prev) => {
      console.log(prev);
      return { ...prev, [name]: value };
    });
    if (userImput.title === "bruno") {
      console.log("pippo");
    }
  };

  // const titleChangeHandler = (event) => {
  //   // setUserImput({...userImput, Title: event.target.value });
  //   setUserImput((preState) => {
  //     return { ...preState, title: event.target.value };
  //   });
  //   // setTitle(event.target.value);
  // };
  // const amountChangeHandler = (event) => {
  //   // setAmount(event.target.value);
  // };
  // const dateChangeHandler = (event) => {
  //   // setDate(event.target.value);
  // };

  const submitHandler = (event) => {
    event.preventDefault();
    // const expenseData = { title: title, amount: amount, date: new Date(date) };
    // console.log(expenseData);
    // setTitle("");
    // setAmount("");
    // setDate("");
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            name="title"
            value={userImput.title}
            type="text"
            onChange={imputChangeHandler}
          />
        </div>

        <div className="new-expense__control">
          <label>Amount</label>
          <input
            value={userImput.amount}
            name="amount"
            type="number"
            // min="0.01"
            // max="0.02"
            onChange={imputChangeHandler}
          />
        </div>

        <div className="new-expense__control">
          <label>Date</label>
          <input
            name="date"
            value={userImput.date}
            type="date"
            onChange={imputChangeHandler}
          />
        </div>
      </div>

      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}
